package L_19_Bit_Masking;

public class Right_Left_shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int x=56;
			System.out.println(x>>3);
			int y = -5 ;
			System.out.println(y>>1);
			System.out.println(y<<1);
			System.out.println(x<<2);
	}

}
